import DropdownMenu from "./modules/dropdown-menu.js"
import BaseHelpers from './helpers/base-helpers.js'

BaseHelpers.calcScrollbarWidth()
BaseHelpers.addLoadedClass()

document.addEventListener('DOMContentLoaded', () => {
	new DropdownMenu()

	new Swiper('.intro .swiper', {
		direction: 'horizontal',
		spaceBetween: 24,
		loop: true,

		autoplay: {
			delay: 5000,
		},
	})

	new Swiper('.offer .swiper', {
		direction: 'horizontal',
		spaceBetween: 16,
		slidesPerView: 5,
		loop: true,

		pagination: {
			el: '.offer .swiper-pagination',
			clickable: true,
		},

		breakpoints: {
			0: {
				slidesPerView: 1,
			},
			768: {
				slidesPerView: 2,
			},
			1024: {
				slidesPerView: 3,
			},
			1280: {
				slidesPerView: 4,
			},
			1440: {
				slidesPerView: 5,
			},
		},
	})
})